			UPDATE gestion_granjas.cat_medidores
			SET
			contrato=p_contrato,
			medidor=p_medidor,
			base_cna=p_basecna,
			fecha_registro=now()
			WHERE
            id_medidor=p_idmedidor;
            
			SET resultado=0;
			set mensaje='¡Se guardaron los cambios correcatmente!!';
		end if;
	else
		/*resultado 4 no existe banco a modificar*/
		set resultado = 2;
        set mensaje='¡el Lote no existe!';
	end if;
