    left join gestion_granjas.cat_colonos c 
    on a.id_colono=c.id_colono 
    where a.lote=p_lote and a.id_manzana=p_idmanzana and a.estatus='A';
elseif(op=3) then
	/*Consulta por nombre del Colono propietario*/
