  -- *******************************************************
  
 -- ******************************************************
 -- ***		Lectura de meses
 -- ******************************************************
	 WHILE CHAR_LENGTH(texto) > 0 AND Pos > 0 DO
	  SET pos = INSTR(texto, delimitador); 
	  IF pos = 0 THEN 
		 SET Str = texto; 
	  ELSE 
		 SET Str = LEFT(texto, pos - 1); 
	  END IF; 
	  
	   IF TRIM(Str) != '' THEN 
	   
			-- Armado de fechas
			
