CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_frm_RegLecturas`(
IN op int,
IN p_idmedidor int,
IN p_periodo int,
IN p_anio int,

OUT o_periodoAnt int,
OUT o_anioAnt int,
OUT o_lectura int,
OUT o_lecturaAnt int
)
BEGIN

/*VARIABLES PARA LECTURAS Y PERIODOS*/
DECLARE v_lectura int;
DECLARE v_lecturaAnt int;
DECLARE v_periodo int;
DECLARE v_periodoAnt int;
DECLARE v_anio int;
DECLARE v_anioAnt int;

if(op=1) then
/*Muestra todos las lecturas y consumos*/
	/*Determinamos periodo y año anterior al ingresado por el usuario*/
    set v_periodoAnt=p_periodo-1;
    set v_anioAnt=p_anio;
    if(v_periodoAnt=0)then
		set v_periodoAnt=12;
		set v_anioAnt=p_anio-1;
	end if;
    
	if exists(SELECT * from gestion_granjas.reg_lecturas WHERE id_medidor=p_idmedidor and periodo=v_periodoAnt and anio=v_anioAnt) then
		SELECT
		periodo,anio
		INTO 
		o_periodoAnt,o_anioAnt
		from gestion_granjas.reg_lecturas 
		WHERE 
		id_medidor=p_idmedidor and periodo=v_periodoAnt and anio=v_anioAnt;
		/*set o_periodoAnt=v_periodo;
		set o_anioAnt=v_anio;*/
		if(o_periodoAnt=0)then
			set o_periodoAnt=12;
			set o_anioAnt=v_anio-1;
		end if;
        if exists(
        SELECT
		lectura
		from gestion_granjas.reg_lecturas 
		WHERE 
		id_medidor=p_idmedidor and periodo=p_periodo and anio=p_anio
        ) then
