  set delimitador=',';
  set texto =pMeses;
  
  SELECT metros_cuadrados into metrosCuadrados FROM gestion_granjas.cat_lotes
  where id_colono=pColono and id_manzana=pManzana and id_lote=pLote;
  
  -- ****************************************************
